package codeChallenges;

public class BA1C {

	/*
	 * This method finds the reverse compliment of a text(genome)
	 */
	public static String reverseCompliment(String Text){
		String outputRev = "";
		String output = "";
		
		for (int i = 0; i < Text.length(); i ++) {
			// Get the reverse of current character
			char ch = Text.charAt(i);
			if (ch == ('A'))
				outputRev += "T";
			else if (ch == 'T')
				outputRev += "A";
			else if (ch == 'C')
				outputRev += "G";
			else if (ch == 'G')
				outputRev += "C";
			else {
				System.out.println("Invalid Char");
			}
		}
		
		// Add the characters to the output
		for (int i = Text.length(); i > 0; i--) {
			output += outputRev.charAt(i - 1);
		}
		
		return output;
	}
	
	public static void main(String[] args) {
		System.out.println(reverseCompliment(""));
		
	}

}
