package assignment1;

public class BA1D {
	
	/*
	 * This method finds patterns in a text and returns how often it occurs
	 */
	public static String patternMatch(String text, String pattern) {
		String output = "";
		
		// Print when we come across the pattern in the nucleotide text
		for (int i = 0; i <= (text.length() - pattern.length()); i ++) {
				if(pattern.equals(text.substring(i, ((pattern.length() + i))))) {
					output += i + " ";
				}
		}
		return output;
	}
	
	public static void main(String[] args) {
		System.out.println(patternMatch("", "CGGTCACCG"));
	}

}
